import React, { useState } from 'react';
import TableComponent from './components/TableComponent'; // 引入表格组件
import NotificationBar from './components/NotificationBar'; // 引入通知条组件

function App() {
    // 示例数据
    const [data, setData] = useState([
        { name: 'John Doe', university: 'Example University', program: 'Computer Science', courseName: 'Data Structures', courseCode: 'CS101', grade: 'A' },
        { name: 'Jane Smith', university: 'Another University', program: 'Mathematics', courseName: 'Algebra', courseCode: 'MATH201', grade: 'B+' },
        { name: 'Emily Johnson', university: 'Sample University', program: 'Physics', courseName: 'Quantum Mechanics', courseCode: 'PHY301', grade: 'A-' },
        { name: 'Michael Brown', university: 'Test University', program: 'Biology', courseName: 'Genetics', courseCode: 'BIO102', grade: 'B' },
    ]);

    const [error, setError] = useState(''); // 错误提示信息

    // 模拟触发错误
    const triggerError = () => {
        setError('Error: Unable to fetch data. Please try again later.');
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            {/* 通知条组件 */}
            <NotificationBar message={error} type="error" />
            <h1>Transcript Manager</h1>
            {/* 按钮用于触发错误 */}
            <button onClick={triggerError} style={{ marginBottom: '20px', padding: '10px 20px' }}>
                Simulate Error
            </button>
            {/* 渲染表格组件，并传递示例数据 */}
            <TableComponent data={data} />
        </div>
    );
}

export default App;
