import React, { useState } from 'react';
import { uploadFile, searchData, downloadData } from './apiService';

function App() {
  const [file, setFile] = useState(null); 
  const [searchCriteria, setSearchCriteria] = useState(''); 
  const [results, setResults] = useState([]); 
  const [uploadStatus, setUploadStatus] = useState(''); 


  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };


  const handleUpload = async () => {
    if (!file) {
      alert('Please select a file first.');
      return;
    }
    try {
      const response = await uploadFile(file);
      alert(`File uploaded: ${response.filename}`);
      setUploadStatus('Upload successful!');
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('Failed to upload file.');
      setUploadStatus('Upload failed.');
    }
  };


  const handleSearch = async () => {
    const criteria = { filename: searchCriteria };
    try {
      const searchResults = await searchData(criteria);
      setResults(searchResults);
    } catch (error) {
      console.error('Error searching data:', error);
      alert('Failed to search data.');
    }
  };


  const handleDownload = async () => {
    try {
      await downloadData();
      alert('File downloaded successfully.');
    } catch (error) {
      console.error('Error downloading file:', error);
      alert('Failed to download file.');
    }
  };

  return (
    <div>
      <div>
        <h1>WU Transcript Management System</h1>
        <p>Welcome! Please upload a file or search for existing data.</p>
      </div>

      {/* Upload Workflow */}
      <h2>Upload File</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      <p>{uploadStatus}</p>

      {/* Search Workflow */}
      <h2>Search Data</h2>
      <input
        type="text"
        placeholder="Enter search criteria (e.g., filename)"
        value={searchCriteria}
        onChange={(e) => setSearchCriteria(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>

      <h3>Search Results:</h3>
      <ul>
        {results.length > 0 ? (
          results.map((result, index) => (
            <li key={index}>
              {result.filename} - {result.status} - {result.size} bytes
            </li>
          ))
        ) : (
          <p>No results to display. Try searching with different criteria.</p>
        )}
      </ul>

      {/* Download Workflow */}
      <h2>Download Data</h2>
      <button onClick={handleDownload}>Download CSV</button>
    </div>
  );
}

export default App;
