import React, { useState, useEffect } from 'react';
import { fetchExistingData, importNewTranscripts } from './api'; // 后端接口文件
import TableComponent from './components/TableComponent'; // 表格组件
import SearchComponent from './components/SearchComponent'; // 搜索组件
import ExportButtons from './components/ExportButtons'; // 导出按钮组件

function App() {
    const [data, setData] = useState([]); // 存储表格数据
    const [originalData, setOriginalData] = useState([]); // 存储原始数据
    const [showComponents, setShowComponents] = useState(false); // 控制是否显示其他组件

    useEffect(() => {
        const loadData = async () => {
            const result = await fetchExistingData();
            setData(result);
            setOriginalData(result); // 保存原始数据以便重置
        };
        loadData();
    }, []);

    const handleImport = async () => {
        const result = await importNewTranscripts();
        setData(result);
        setOriginalData(result);
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>Transcript Manager</h1>
            {/* 条件渲染：只有当 showComponents 为 true 时，才显示这些组件 */}
            {showComponents && (
                <>
                    <SearchComponent
                        searchQuery={searchQuery}
                        setSearchQuery={setSearchQuery}
                        handleSearch={handleSearch}
                    />
                    <ExportButtons data={data} />
                    <button onClick={handleImport} style={{ margin: '20px 0', padding: '10px 20px' }}>
                        Import New Data
                    </button>
                </>
            )}
            {/* 永远显示表格组件 */}
            <TableComponent data={data} />
        </div>
    );
}

export default App;
