import React from 'react';

function NotificationBar({ message, type }) {
    const barStyle = {
        padding: '10px',
        textAlign: 'center',
        color: 'white',
        fontWeight: 'bold',
        backgroundColor: type === 'error' ? 'red' : 'green',
        position: 'fixed',
        top: 0,
        width: '100%',
        zIndex: 1000,
    };

    return message ? <div style={barStyle}>{message}</div> : null;
}

export default NotificationBar;
