import React from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
    return (
        <div style={{ textAlign: 'center', padding: '40px', fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f7f6' }}>
            <h1 style={{ fontSize: '3em', color: '#333' }}>Transcript Manager</h1>
            <p style={{ fontSize: '1.2em', color: '#555', marginBottom: '40px' }}>
                Welcome to the Transcript Manager! Choose a feature to get started:
            </p>
            <div style={{ margin: '20px 0' }}>
                <Link to="/upload" style={{ textDecoration: 'none' }}>
                    <button
                        style={{
                            marginRight: '15px',
                            padding: '12px 25px',
                            fontSize: '1.1em',
                            backgroundColor: '#007BFF',
                            color: '#fff',
                            border: 'none',
                            borderRadius: '5px',
                            cursor: 'pointer',
                            transition: 'background-color 0.3s ease',
                        }}
                        onMouseOver={(e) => e.target.style.backgroundColor = '#0056b3'}
                        onMouseOut={(e) => e.target.style.backgroundColor = '#007BFF'}
                    >
                        Upload Transcript
                    </button>
                </Link>
                <Link to="/query" style={{ textDecoration: 'none' }}>
                    <button
                        style={{
                            padding: '12px 25px',
                            fontSize: '1.1em',
                            backgroundColor: '#28a745',
                            color: '#fff',
                            border: 'none',
                            borderRadius: '5px',
                            cursor: 'pointer',
                            transition: 'background-color 0.3s ease',
                        }}
                        onMouseOver={(e) => e.target.style.backgroundColor = '#218838'}
                        onMouseOut={(e) => e.target.style.backgroundColor = '#28a745'}
                    >
                        Query and Download
                    </button>
                </Link>
            </div>
        </div>
    );
}

export default HomePage;
