// src/api.js
export const mockData = [
    { educatorname: 'John Doe', credits_earned: 3, grade: 'A', shoude_be_category: 'Accounting' },
    { educatorname: 'Jane Smith', credits_earned: 4, grade: 'B', shoude_be_category: 'Finance' },
    { educatorname: 'Michael Brown', credits_earned: 3, grade: 'A-', shoude_be_category: 'Marketing' },
    { educatorname: 'Emily White', credits_earned: 2, grade: 'B+', shoude_be_category: 'IT General' },
    { educatorname: 'David Green', credits_earned: 5, grade: 'A+', shoude_be_category: 'Economics' }
];

// 模拟获取数据
export const fetchExistingData = async () => {
    return new Promise((resolve) => {
        setTimeout(() => {
            resolve(mockData);
        }, 500); // 模拟 500ms 延迟
    });
};

// 模拟 API 进行查询
export const querySearchData = async ({ query, category }) => {
    return new Promise((resolve) => {
        setTimeout(() => {
            const filteredData = mockData.filter((item) => {
                const matchesQuery = query ? 
                    Object.values(item).some(value => 
                        value.toString().toLowerCase().includes(query.toLowerCase())
                    ) : true;

                const matchesCategory = category ? item.shoude_be_category === category : true;

                return matchesQuery && matchesCategory;
            });
            resolve(filteredData);
        }, 300); // 模拟 300ms 延迟
    });
};
