import React, { useState } from 'react';

function App() {
    const [searchQuery, setSearchQuery] = useState('');
    const [data, setData] = useState([
        { name: 'John Doe', university: 'Example University', program: 'Computer Science', courseName: 'Data Structures', courseCode: 'CS101', grade: 'A' },
        { name: 'Jane Smith', university: 'Another University', program: 'Mathematics', courseName: 'Algebra', courseCode: 'MATH201', grade: 'B+' },
    ]);

    const handleSearch = () => {
        const filteredData = data.filter((item) =>
            Object.values(item).some((value) => value.toLowerCase().includes(searchQuery.toLowerCase()))
        );
        setData(filteredData);
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>Transcript Manager</h1>
            <div style={{ marginBottom: '20px' }}>
                <input
                    type="text"
                    placeholder="Search by any field"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    style={{ padding: '5px', width: '300px' }}
                />
                <button onClick={handleSearch} style={{ marginLeft: '10px', padding: '5px 10px' }}>Search</button>
            </div>
            <table border="1" style={{ margin: '20px auto', width: '80%' }}>
                <thead>
                    <tr>
                        <th>Student Name</th>
                        <th>University</th>
                        <th>Program</th>
                        <th>Course Name</th>
                        <th>Course Code</th>
                        <th>Grade</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item, index) => (
                        <tr key={index}>
                            <td>{item.name}</td>
                            <td>{item.university}</td>
                            <td>{item.program}</td>
                            <td>{item.courseName}</td>
                            <td>{item.courseCode}</td>
                            <td>{item.grade}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default App;
